mapper.update:

update message
set read_date = sysdate, recv_read = 'Y'
where mms_no = #{mms_no}

update message
set recv_del = 'Y'
where mms_no = #{mms_no}

update message
set sent_del = 'Y'
where mms_no = #{mms_no}

update message
set recv_show = 'Y'
where mms_no = #{mms_no}

update message
set sent_show = 'Y'
where mms_no = #{mms_no}

update message
set recv_del = 'N'
where mms_no = #{mms_no}

update message
set sent_del = 'N'
where mms_no = #{mms_no}
